UOSP.sendKalingImage = function sendKalingImage(room, imageURL, URL, description,button,width, height){
    var kalingObj={
        "link_ver": "4.0",
        "template_object": {
            "object_type": "feed",
            "content": {
                "title": "",
                "image_url": imageURL,
                "image_width": width||0,
                "image_height": height||0,
                "link": {
                    "web_url": URL,
                    "mobile_web_url": URL
                },
                "description": description || ""
            },
            "buttons": [{
                "title": button || "",
                "link": {
                    "web_url": URL,
                    "mobile_web_url": URL
                }
            }]
        }
    };
    try{
        Kakao.send(room, kalingObj );
    }catch(e){
        UOSP.kakaoReset();
        Kakao.send(room, kalingObj );
    }

}

